import React, { useState, useEffect } from 'react';
import api from '../api'; // your axios instance
import './Purchases.css';

const Purchases = () => {
  const [purchases, setPurchases] = useState([]);
  const [assets, setAssets] = useState([]);
  const [filters, setFilters] = useState({ date: '', type: 'All' });
  const [newPurchase, setNewPurchase] = useState({ asset: '', quantity: 0, base: '' });

  useEffect(() => {
    fetchAssets();
    fetchPurchases();
  }, [filters]);

  const fetchAssets = async () => {
    try {
      const res = await api.get('/assets');
      setAssets(res.data);
    } catch (err) {
      console.error('Failed to fetch assets:', err);
    }
  };

  const fetchPurchases = async () => {
    try {
      const query = [];
      if (filters.date) query.push(`date=${filters.date}`);
      if (filters.type !== 'All') query.push(`type=${filters.type}`);
      const res = await api.get(`/purchases?${query.join('&')}`);
      setPurchases(res.data);
    } catch (err) {
      console.error('Failed to fetch purchases:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/purchases', newPurchase);
      fetchPurchases();
      setNewPurchase({ asset: '', quantity: 0, base: '' });
    } catch (err) {
      console.error('Failed to create purchase:', err);
    }
  };

  const handleFilterChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Purchases</h2>

      {/* Add Purchase Form */}
      <form onSubmit={handleSubmit} className="mb-6 space-y-2">
        <select
          value={newPurchase.asset}
          onChange={(e) => setNewPurchase({ ...newPurchase, asset: e.target.value })}
          required
        >
          <option value="">Select Asset</option>
          {assets.map((a) => (
            <option key={a._id} value={a._id}>
              {a.name}
            </option>
          ))}
        </select>
        <input
          type="number"
          placeholder="Quantity"
          value={newPurchase.quantity}
          onChange={(e) => setNewPurchase({ ...newPurchase, quantity: e.target.value })}
          required
        />
        <input
          placeholder="Base ID"
          value={newPurchase.base}
          onChange={(e) => setNewPurchase({ ...newPurchase, base: e.target.value })}
          required
        />
        <button type="submit">Add Purchase</button>
      </form>

      {/* Filters */}
      <div className="mb-4 space-x-2">
        <input
          type="date"
          name="date"
          value={filters.date}
          onChange={handleFilterChange}
        />
        <select name="type" value={filters.type} onChange={handleFilterChange}>
          <option>All</option>
          <option>Weapon</option>
          <option>Vehicle</option>
          <option>Ammunition</option>
          <option>Equipment</option>
        </select>
      </div>

      {/* Purchase List */}
      <ul>
        {purchases.map((p) => (
          <li key={p._id}>
            {p.asset?.assetname || 'Unknown'} - {p.quantity} units on {new Date(p.date).toDateString()}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Purchases;
