import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Purchases.css';
import api from '../api';
const Purchases = () => {
  const [purchases, setPurchases] = useState([]);
  const [newPurchase, setNewPurchase] = useState({ asset: '', quantity: 0, base: '' });

  useEffect(() => {
    fetchPurchases();
  }, []);

  const fetchPurchases = async () => {
    const res = await api.get('/purchases');
    setPurchases(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
   await api.post('/purchases', newPurchase);

    fetchPurchases();
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Purchases</h2>
      <form onSubmit={handleSubmit} className="mb-6 space-y-2">
        <input placeholder="Asset ID" value={newPurchase.asset} onChange={(e) => setNewPurchase({ ...newPurchase, asset: e.target.value })} />
        <input placeholder="Quantity" type="number" value={newPurchase.quantity} onChange={(e) => setNewPurchase({ ...newPurchase, quantity: e.target.value })} />
        <input placeholder="Base ID" value={newPurchase.base} onChange={(e) => setNewPurchase({ ...newPurchase, base: e.target.value })} />
        <button type="submit">Add Purchase</button>
      </form>
      <ul>
        {purchases.map(p => (
          <li key={p._id}>{p.asset.name} - {p.quantity} on {new Date(p.date).toDateString()}</li>
        ))}
      </ul>
    </div>
  );
};

export default Purchases;
