import React, { useState } from 'react';
import AssetList from './AssetList';
import './Assets.css';

const Assets = () => {
  const [filters, setFilters] = useState({
    date: '',
    base: 'All Bases',
    type: 'All Types'
  });

  const assets = [
    { name: 'Humvee', type: 'Vehicle', quantity: 15, status: 'In stock', base: 'Base Alpha' },
    { name: 'M4 Carbine', type: 'Weapon', quantity: 12, status: 'Assigned', base: 'Base Alpha' },
    { name: 'Artillery Shell', type: 'Ammunition', quantity: 300, status: 'In stock', base: 'Base Bravo' },
    { name: 'Radio', type: 'Equipment', quantity: 10, status: 'Maintenance', base: 'Base Charlie' },
  ];

  // Filter assets according to filters
  const filteredAssets = assets.filter(asset => {
    return (filters.base === 'All Bases' || asset.base === filters.base)
      && (filters.type === 'All Types' || asset.type === filters.type);
  });

  const handleFilterChange = e => {
    const { name, value } = e.target;
    setFilters(prev => ({ ...prev, [name]: value }));
  };

  return (
    <div>
      <h1>Assets</h1>
      <div className="filters">
        <input
          type="date"
          name="date"
          value={filters.date}
          onChange={handleFilterChange}
          placeholder="Select date"
          className="filter-input"
        />
        <select name="base" value={filters.base} onChange={handleFilterChange} className="filter-select">
          <option>All Bases</option>
          <option>Base Alpha</option>
          <option>Base Bravo</option>
          <option>Base Charlie</option>
        </select>
        <select name="type" value={filters.type} onChange={handleFilterChange} className="filter-select">
          <option>All Types</option>
          <option>Vehicle</option>
          <option>Weapon</option>
          <option>Ammunition</option>
          <option>Equipment</option>
        </select>
      </div>
      <AssetList assets={filteredAssets} />
    </div>
  );
};

export default Assets;
