import React, { useState, useEffect } from 'react';
import api from '../api';
import './Purchases.css'; // Optional: use Tailwind or update CSS as needed

const Purchases = () => {
  const [purchases, setPurchases] = useState([]);
  const [assets, setAssets] = useState([]);
  const [filters, setFilters] = useState({ date: '', type: 'All' });
  const [newPurchase, setNewPurchase] = useState({ asset: '', quantity: 0, base: '' });

  // Fetch assets first
  useEffect(() => {
    fetchAssets();
  }, []);

  // Fetch purchases after assets load
  useEffect(() => {
    if (assets.length > 0) {
      fetchPurchases();
    }
  }, [assets]);

  const fetchAssets = async () => {
    try {
      const res = await api.get('/assets');
      setAssets(res.data);
    } catch (err) {
      console.error('Failed to fetch assets:', err);
    }
  };

  const fetchPurchases = async () => {
    try {
      const res = await api.get('/purchases');
      setPurchases(res.data);
    } catch (err) {
      console.error('Failed to fetch purchases:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/purchases', newPurchase);
      fetchPurchases();
      setNewPurchase({ asset: '', quantity: 0, base: '' });
    } catch (err) {
      console.error('Failed to create purchase:', err);
    }
  };

  const handleFilterChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  const getAssetById = (assetId) => assets.find((a) => a._id === assetId);
  const getAssetName = (assetId) => getAssetById(assetId)?.name || 'Unknown';
  const getAssetType = (assetId) => getAssetById(assetId)?.type || 'Unknown';

  // Apply filters
  const filteredPurchases = purchases.filter((p) => {
    const matchesDate =
      !filters.date || new Date(p.date).toISOString().slice(0, 10) === filters.date;
    const matchesType =
      filters.type === 'All' || getAssetType(p.asset) === filters.type;
    return matchesDate && matchesType;
  });

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-6">Purchases</h2>

      {/* Form */}
      <form onSubmit={handleSubmit} className="grid grid-cols-1 sm:grid-cols-3 gap-4 mb-8">
        <select
          value={newPurchase.asset}
          onChange={(e) => setNewPurchase({ ...newPurchase, asset: e.target.value })}
          required
          className="p-2 border rounded"
        >
          <option value="">Select Asset</option>
          {assets.map((a) => (
            <option key={a._id} value={a._id}>
              {a.name}
            </option>
          ))}
        </select>
        <input
          type="number"
          placeholder="Quantity"
          value={newPurchase.quantity}
          onChange={(e) => setNewPurchase({ ...newPurchase, quantity: e.target.value })}
          required
          className="p-2 border rounded"
        />
        <input
          placeholder="Base ID"
          value={newPurchase.base}
          onChange={(e) => setNewPurchase({ ...newPurchase, base: e.target.value })}
          required
          className="p-2 border rounded"
        />
        <button
          type="submit"
          className="col-span-1 sm:col-span-3 bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
        >
          Add Purchase
        </button>
      </form>

      {/* Filters */}
      <div className="flex flex-wrap gap-4 mb-6">
        <input
          type="date"
          name="date"
          value={filters.date}
          onChange={handleFilterChange}
          className="p-2 border rounded"
        />
        <select
          name="type"
          value={filters.type}
          onChange={handleFilterChange}
          className="p-2 border rounded"
        >
          <option>All</option>
          <option>Weapon</option>
          <option>Vehicle</option>
          <option>Ammunition</option>
          <option>Equipment</option>
        </select>
      </div>

      {/* Purchase List */}
      <div className="bg-white shadow-md rounded-lg overflow-hidden">
        <table className="min-w-full table-auto">
          <thead className="bg-gray-100 text-left">
            <tr>
              <th className="p-3">Asset</th>
              <th className="p-3">Type</th>
              <th className="p-3">Quantity</th>
              <th className="p-3">Date</th>
              <th className="p-3">Base</th>
            </tr>
          </thead>
          <tbody>
            {filteredPurchases.length > 0 ? (
              filteredPurchases.map((p) => (
                <tr key={p._id} className="border-t">
                  <td className="p-3">{getAssetName(p.asset)}</td>
                  <td className="p-3">{getAssetType(p.asset)}</td>
                  <td className="p-3">{p.quantity}</td>
                  <td className="p-3">{new Date(p.date).toDateString()}</td>
                  <td className="p-3">{p.base}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5" className="text-center p-4 text-gray-500">
                  No purchases found.
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Purchases;
