import React, { useState, useEffect } from 'react';
import api from '../api'; // axios instance
import './Purchases.css';

const Purchases = () => {
  const [purchases, setPurchases] = useState([]);
  const [assets, setAssets] = useState([]);
  const [newPurchase, setNewPurchase] = useState({
    asset: '',
    quantity: 0,
    base: '',
  });

  useEffect(() => {
    fetchPurchases();
    fetchAssets();
  }, []);

  const fetchPurchases = async () => {
    try {
      const res = await api.get('/purchases');
      setPurchases(res.data);
    } catch (err) {
      console.error('Failed to fetch purchases:', err);
    }
  };

  const fetchAssets = async () => {
    try {
      const res = await api.get('/assets');
      setAssets(res.data);
    } catch (err) {
      console.error('Failed to fetch assets:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/purchases', newPurchase);
      fetchPurchases();
      setNewPurchase({ asset: '', quantity: 0, base: '' }); // Clear form
    } catch (err) {
      console.error('Failed to create purchase:', err);
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Purchases</h2>

      <form onSubmit={handleSubmit} className="mb-6 space-y-2">
        {/* Asset dropdown */}
        <select
          value={newPurchase.asset}
          onChange={(e) => setNewPurchase({ ...newPurchase, asset: e.target.value })}
          required
        >
          <option value="">Select Asset</option>
          {assets.map(asset => (
            <option key={asset._id} value={asset._id}>
              {asset.name}
            </option>
          ))}
        </select>

        {/* Quantity */}
        <input
          type="number"
          placeholder="Quantity"
          value={newPurchase.quantity}
          onChange={(e) =>
            setNewPurchase({ ...newPurchase, quantity: parseInt(e.target.value, 10) })
          }
          required
        />

        {/* Base ID input */}
        <input
          placeholder="Base ID"
          value={newPurchase.base}
          onChange={(e) => setNewPurchase({ ...newPurchase, base: e.target.value })}
          required
        />

        <button type="submit">Add Purchase</button>
      </form>

      {/* Purchases list */}
      <ul>
        {purchases.map((p) => (
          <li key={p._id}>
            {p.asset ? `${p.asset.name} - ${p.quantity} on ${new Date(p.date).toDateString()}` : 'Unknown Asset'}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Purchases;
