import React, { useState, useEffect } from 'react';
import api from '../api';
import './Purchases.css';

const Purchases = () => {
  const [purchases, setPurchases] = useState([]);
  const [assets, setAssets] = useState([]);
  const [filters, setFilters] = useState({ date: '', type: 'All' });
  const [newPurchase, setNewPurchase] = useState({ asset: '', quantity: 0, base: '' });

  useEffect(() => {
    fetchAssets();
    fetchPurchases();
  }, []);

  const fetchAssets = async () => {
    try {
      const res = await api.get('/assets');
      setAssets(res.data);
    } catch (err) {
      console.error('Failed to fetch assets:', err);
    }
  };

  const fetchPurchases = async () => {
    try {
      const res = await api.get('/purchases');
      setPurchases(res.data);
    } catch (err) {
      console.error('Failed to fetch purchases:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/purchases', newPurchase);
      fetchPurchases();
      setNewPurchase({ asset: '', quantity: 0, base: '' });
    } catch (err) {
      console.error('Failed to create purchase:', err);
    }
  };

  const handleFilterChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  const getAssetById = (id) => assets.find((a) => a._id === id);

  const getAssetName = (id, fallback) => {
    const asset = getAssetById(id);
    return asset?.name || fallback || 'Unknown';
  };

  const getAssetType = (id) => {
    const asset = getAssetById(id);
    return asset?.type || 'Unknown';
  };

  const filteredPurchases = purchases.filter((p) => {
    const matchesDate = !filters.date || new Date(p.date).toISOString().slice(0, 10) === filters.date;
    const matchesType = filters.type === 'All' || getAssetType(p.asset) === filters.type;
    return matchesDate && matchesType;
  });

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-6">Purchases</h2>

      {/* Form */}
      <form onSubmit={handleSubmit} className="grid grid-cols-1 sm:grid-cols-4 gap-4 mb-6 bg-gray-50 p-4 rounded shadow">
        <select
          value={newPurchase.asset}
          onChange={(e) => setNewPurchase({ ...newPurchase, asset: e.target.value })}
          required
          className="border p-2 rounded"
        >
          <option value="">Select Asset</option>
          {assets.map((a) => (
            <option key={a._id} value={a._id}>
              {a.name}
            </option>
          ))}
        </select>
        <input
          type="number"
          min="1"
          placeholder="Quantity"
          value={newPurchase.quantity}
          onChange={(e) => setNewPurchase({ ...newPurchase, quantity: e.target.value })}
          required
          className="border p-2 rounded"
        />
        <input
          placeholder="Base ID"
          value={newPurchase.base}
          onChange={(e) => setNewPurchase({ ...newPurchase, base: e.target.value })}
          required
          className="border p-2 rounded"
        />
        <button type="submit" className="bg-blue-600 text-white rounded px-4 py-2 hover:bg-blue-700">
          Add Purchase
        </button>
      </form>

      {/* Filters */}
      <div className="flex flex-wrap items-center gap-4 mb-4">
        <input
          type="date"
          name="date"
          value={filters.date}
          onChange={handleFilterChange}
          className="border p-2 rounded"
        />
        <select
          name="type"
          value={filters.type}
          onChange={handleFilterChange}
          className="border p-2 rounded"
        >
          <option value="All">All Types</option>
          <option value="Weapon">Weapon</option>
          <option value="Vehicle">Vehicle</option>
          <option value="Ammunition">Ammunition</option>
          <option value="Equipment">Equipment</option>
        </select>
      </div>

      {/* Purchases List */}
      <div className="space-y-3">
        {filteredPurchases.length === 0 ? (
          <p className="text-gray-500">No purchases found for the selected filters.</p>
        ) : (
          filteredPurchases.map((p) => {
            const assetName = getAssetName(p.asset, p.assetName);
            const assetType = getAssetType(p.asset);
            const date = new Date(p.date).toDateString();
            return (
              <div
                key={p._id}
                className="bg-white shadow-sm p-4 rounded flex justify-between items-center border"
              >
                <div>
                  <p className="font-semibold text-lg">{assetName}</p>
                  <p className="text-sm text-gray-600">
                    Type: {assetType} | Quantity: {p.quantity} | Date: {date}
                  </p>
                </div>
                <span className="text-sm text-gray-500">Base: {p.base}</span>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
};

export default Purchases;
