import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Transfers.css';

const Transfers = () => {
  const [transfers, setTransfers] = useState([]);
  const [newTransfer, setNewTransfer] = useState({ asset: '', quantity: 0, fromBase: '', toBase: '' });

  useEffect(() => {
    fetchTransfers();
  }, []);

  const fetchTransfers = async () => {
    const res = await axios.get('/api/transfers');
    setTransfers(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('/api/transfers', newTransfer);
    fetchTransfers();
  };

  return (
    <div className="transfers-container">
      <h2>Transfers</h2>
      <form onSubmit={handleSubmit}>
        <input
          placeholder="Asset ID"
          value={newTransfer.asset}
          onChange={(e) => setNewTransfer({ ...newTransfer, asset: e.target.value })}
          required
        />
        <input
          placeholder="Quantity"
          type="number"
          value={newTransfer.quantity}
          onChange={(e) => setNewTransfer({ ...newTransfer, quantity: e.target.value })}
          required
        />
        <input
          placeholder="From Base ID"
          value={newTransfer.fromBase}
          onChange={(e) => setNewTransfer({ ...newTransfer, fromBase: e.target.value })}
          required
        />
        <input
          placeholder="To Base ID"
          value={newTransfer.toBase}
          onChange={(e) => setNewTransfer({ ...newTransfer, toBase: e.target.value })}
          required
        />
        <button type="submit">Transfer</button>
      </form>
      <ul>
        {transfers.map(t => (
          <li key={t._id}>
            {t.asset.name} - {t.quantity} from {t.fromBase.name} to {t.toBase.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Transfers;
