import React, { useState, useEffect } from 'react';
import api from '../api';
import './Assets.css';

const Assets = () => {
  const [filters, setFilters] = useState({
    date: '',
    base: 'All Bases',
    type: 'All Types'
  });
  const [assets, setAssets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [newAsset, setNewAsset] = useState({
    name: '',
    type: 'Vehicle',
    quantity: 1,
    base: ''
  });

  useEffect(() => {
    fetchAssets();
  }, []);

  const fetchAssets = async () => {
    try {
      const res = await api.get('/assets');
      setAssets(res.data);
    } catch (err) {
      setError('Failed to load assets');
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prev) => ({ ...prev, [name]: value }));
  };

  const handleNewAssetChange = (e) => {
    const { name, value } = e.target;
    setNewAsset((prev) => ({ ...prev, [name]: value }));
  };

  const handleAddAsset = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/assets', newAsset);
      setAssets((prev) => [...prev, res.data]);
      setNewAsset({ name: '', type: 'Vehicle', quantity: 1, base: '' });
    } catch (err) {
      alert('Failed to add asset');
    }
  };

  const filteredAssets = assets.filter((asset) => {
    return (
      (filters.base === 'All Bases' || asset.base?.name === filters.base) &&
      (filters.type === 'All Types' || asset.type === filters.type)
    );
  });

  if (loading) return <p className="main-content">Loading assets...</p>;
  if (error) return <p className="main-content">{error}</p>;

  return (
    <div className="main-content">
      <h1>Assets</h1>

      {/* Add Asset Form */}
      <form className="add-asset-form" onSubmit={handleAddAsset}>
        <input
          type="text"
          name="name"
          placeholder="Asset Name"
          value={newAsset.name}
          onChange={handleNewAssetChange}
          required
        />
        <select name="type" value={newAsset.type} onChange={handleNewAssetChange}>
          <option value="Vehicle">Vehicle</option>
          <option value="Weapon">Weapon</option>
          <option value="Ammunition">Ammunition</option>
        </select>
        <input
          type="number"
          name="quantity"
          min="1"
          value={newAsset.quantity}
          onChange={handleNewAssetChange}
        />
        <input
          type="text"
          name="base"
          placeholder="Base ID"
          value={newAsset.base}
          onChange={handleNewAssetChange}
          required
        />
        <button type="submit">Add Asset</button>
      </form>

      {/* Filters */}
      <div className="filter-container">
        <input
          type="date"
          name="date"
          value={filters.date}
          onChange={handleFilterChange}
          className="filter-input"
        />
        <select name="base" value={filters.base} onChange={handleFilterChange} className="filter-select">
          <option>All Bases</option>
          <option>Alpha Base</option>
          <option>Bravo Base</option>
          <option><option>Bravo Base</option> Base</option>
        </select>
        <select name="type" value={filters.type} onChange={handleFilterChange} className="filter-select">
          <option>All Types</option>
          <option>Vehicle</option>
          <option>Weapon</option>
          <option>Ammunition</option>
        </select>
      </div>

      {/* Table */}
      <table className="asset-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Type</th>
            <th>Quantity</th>
            <th>Base</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredAssets.map((asset) => (
            <tr key={asset._id}>
              <td>{asset.name}</td>
              <td>{asset.type}</td>
              <td>{asset.quantity}</td>
              <td>{asset.base?.name || asset.base}</td>
              <td className="action-buttons">
                <button className="edit-btn">Edit</button>
                <button className="delete-btn">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Assets;
